<?php
class api{		    protected function apiDataGet($url, $json = false, $token = null){        $curl = curl_init();                if ($token != null){            $headers = [                'Origin: https://developer.riotgames.com',                'Accept-Charset: application/x-www-form-urlencoded; charset=UTF-8',                'X-Riot-Token: '. $token,                'Accept-Language: pt-BR,pt;q=0.9,ja-JP;q=0.8,ja;q=0.7,en-US;q=0.6,en;q=0.5',                'User-Agent: Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/79.0.3945.88 Safari/537.36'            ];            curl_setopt($curl, CURLOPT_HTTPHEADER, $headers);        }        curl_setopt($curl, CURLOPT_URL, "$url");        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);        curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, false);        curl_setopt($curl, CURLOPT_FAILONERROR, false);        $resultado = curl_exec($curl);        curl_close($curl);                if ($json == true){            return $resultado;        } else {            return json_decode($resultado, true);        }    }	    protected function myfileget($url){		$curl = curl_init();			curl_setopt($curl, CURLOPT_URL, "$url");			curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);			curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, false);			curl_setopt($curl, CURLOPT_FAILONERROR, false);		$resultado = curl_exec($curl);		curl_close($curl);		return $resultado;	}		protected function apiDataGetLocal($arquivo_caminho){	    if (file_exists($arquivo_caminho)){	        $string = file_get_contents($arquivo_caminho);// 	        $json = json_decode($string, true);	        return $string;	    } else {	        return false;	    }            	}		function data_hoje(){	    date_default_timezone_set("America/Sao_Paulo");	    $data = date('Ymd');	    return $data;	}		function data($epoch){		date_default_timezone_set("America/Sao_Paulo");		$data = date('d/m/Y', $epoch/1000);		return $data;	}		function hora($epoch){		date_default_timezone_set("America/Sao_Paulo");		$hora = date('H:m:s', $epoch/1000);		return $hora;	}		function data_hora($epoch){		date_default_timezone_set("America/Sao_Paulo");		$data_hora = date('d/m/Y H:m:s', $epoch/1000);		return $data_hora;	}}?>